import 'react-native-gesture-handler';
import { StyleSheet, Text, View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import React from 'react';

import ActiveHunt from './views/ActiveHunt';
import AddFriend from './views/AddFriend';
import ChangeCredentials from './views/ChangeCredentials';
import Dashboard from './views/Dashboard';
import CreateHunt from './views/CreateHunt';
import FindHunts from './views/FindHunts';
import Friends from './views/Friends';
import FriendRequests from './views/FriendRequests';
import FriendProfile from './views/FriendProfile';
import HuntInfo from './views/HuntInfo';
import Hunts from './views/Hunts';
import Login from './views/Login';
import Profile from './views/Profile';
import Settings from './views/Settings';
import SignUp from './views/SignUp';



const Stack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();



export default function App() {

const [loggedIn, setLoggedIn] = React.useState(false);


  if (loggedIn) {
    return (
      <NavigationContainer>
        <Drawer.Navigator initialRouteName='Dashboard' >
          <Drawer.Group screenOptions={{headerTitle: ''}}>
            <Drawer.Screen 
              name='Profile'
              component={Profile}
            />
            <Drawer.Screen 
              name='Dashboard'
              component={Dashboard}
            />
            <Drawer.Screen 
              name='Hunts'
              component={Hunts}
            />
            <Drawer.Screen 
              name='Friends'
              component={Friends}
            />
          </Drawer.Group>
        </Drawer.Navigator>
      </NavigationContainer>
    );
  }
  else {
    return (
      <Login setLoggedIn={setLoggedIn} />
    )
  }
  
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
