import { LinearGradient } from "expo-linear-gradient";

const Styles = {
    NavHeaderStyle : {
        headerTitleAlign: 'center',
        headerTitle: 'Lookout!',
        headerTintColor: 'white',
        headerTitleStyle: {fontSize: 40, fontWeight: 'bold'},
        headerBackground: 
        () => (<LinearGradient 
              colors={['#53ECFC', '#F283B3']}
              style={{ flex: 1 }}
              start={{x: 0, y: 0}}
              end={{x: 0, y: 1}}
        />)
      },

}

export default Styles;
